package activemq.test;
import javax.jms.Connection;
import javax.jms.ConnectionFactory;
import javax.jms.DeliveryMode;
import javax.jms.Destination;
import javax.jms.MessageProducer;
import javax.jms.Session;
import javax.jms.TextMessage;

import org.apache.activemq.ActiveMQConnectionFactory;

/**
 * 发送者
 * 1：首先建立连接，用户名、密码、tcp地址
 * 2：创建连接，然后打开连接
 * 3：创建Session会话，关联着生产者消费者以及消息的目的地，
 * 	    创建Session时可指定是否需要事务以及签收方式，以及创建消息类型
 * 4：通过Session创建Destination，用来存放消息的目的地
 * 5：创建生产者
 * 6：使用生产者发送消息
 */
public class SenderString {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
	public static void main(String[] args) throws Exception{
		// 1：建立ConnectionFactory对象，需要用户名、密码已经连接地址(默认tcp://localhost:61616)
		ConnectionFactory factory = new ActiveMQConnectionFactory(ActiveMQConnectionFactory.DEFAULT_USER, 
				ActiveMQConnectionFactory.DEFAULT_PASSWORD, "tcp://localhost:61616");
		
		// 2：通过ConnectionFactory对象创建Connection连接，并调用start方法开启连接，默认是关闭的
		Connection connection = factory.createConnection();
		connection.start();
		
		// 3：通过Connection对象创建Session会话(上下文环境对象)，用户接受消息，参数1是否启用事务，参数2为签收模式
//		Session session = connection.createSession(Boolean.FALSE, Session.AUTO_ACKNOWLEDGE);
//		Session session = connection.createSession(Boolean.TRUE, Session.AUTO_ACKNOWLEDGE);
		// 设置为手动签收模式，必须手动签收
		Session session = connection.createSession(Boolean.FALSE, Session.CLIENT_ACKNOWLEDGE);
		
		/*
		 * 4：通过Session创建Destination对象，指一个客户端用来指定生产消息目标和消费消息来源对象，存放消息的地方
		 * 在PTP模式中，Destination被称作Queue，在Pub/Sub中，被称作Topic既是主题，可以出现多个
		 */
		Destination destination = session.createQueue("testQueue"); // 队列的名字
		
		/*
		 * 5：通过Session对象创建消息的发送和接受对象(生产者和消费者)MessageProducer/MessageConsumer，消息放入
		 *	上一步创建的Destination
		 */
//		MessageProducer producer = session.createProducer(destination);
		MessageProducer producer = session.createProducer(null);
		
		// 6：使用生产者的setDeliveryMode方法设置持久和非持久特性
//		producer.setDeliveryMode(DeliveryMode.NON_PERSISTENT);
		
		// 7：用JMS规范的TextMessage形式创建数据(通过Session对象)，使用生产者的send方法发送数据
//		TextMessage message = session.createTextMessage();
		TextMessage m1 = session.createTextMessage();
		m1.setText("消息内容1");
		producer.send(destination, m1, DeliveryMode.NON_PERSISTENT, 1, 1000*60*10);
		TextMessage m2 = session.createTextMessage();
		m2.setText("消息内容8");
		producer.send(destination, m2, DeliveryMode.NON_PERSISTENT, 8, 1000*60*10);
		TextMessage m3 = session.createTextMessage();
		m3.setText("消息内容4");
		producer.send(destination, m3, DeliveryMode.NON_PERSISTENT, 4, 1000*60*10);
		
		/*for (int i = 0; i < 5; i++) {
			message.setText("测试发送消息:"+i);
//			producer.send(message);
			// 参数一：消息发送目的地
			// 参数二：消息内容
			// 参数三： 是否持久化
			// 参数四：优先级(0-9 0-4普通 5-9加急 默认4)
			producer.send(destination, message, DeliveryMode.NON_PERSISTENT, i, 1000*60*2);
			TimeUnit.MILLISECONDS.sleep(10);
		}*/
//		session.commit();
		if(connection != null){
			connection.close();
		}
	}
}
